import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { useEffect, useState } from "react";
import Style from './App.module.css';
import axios from 'axios';
// component
import Header from './component/Header/Header';
// page
import Signup from './page/signup/Signup';
import Signin from './page/signin/Signin';
import Profile from './page/profile/Profile';
import Main from './page/main/Main';
import NotFound from './page/notfound/NotFound';
function App() {
  const [user, setUser] = useState('');
  const [login ,setLogin] = useState(false);

  const [data, setData] = useState([]);

  const [act, setAct] = useState([]);

  const getData = async () => {
    try{
        await axios.get("/todos",{
            headers: {
                Authorization: `Bearer ${localStorage.getItem("token")}`
            }
        })
        .then(res => 
            {
                setData(Object.values(res.data.datas).map(item => ({
                    ...item,
                    date: item.date.split('T')[0]
                }))
                .sort((date1, date2) => {
                    return new Date(date1.date) - new Date(date2.date); // Date로 전환 -
                }));
                console.log(Object.values(res.data.datas).map(item => ({
                    ...item,
                    date: item.date.split('T')[0]
                }))
                .sort((date1, date2) => {
                    return new Date(date1.date) - new Date(date2.date);
                }));
            })

        .catch(err => console.error(err.response))

    }catch(err){
        console.error(err);
    }
}


  useEffect(() => {
        // 페이지 로드 시 토큰 확인
        const token = localStorage.getItem('token');
        const savedUser = localStorage.getItem('user');

        if (token && savedUser) {
            setLogin(true);
            setUser(JSON.parse(savedUser));

            // axios 헤더 토큰
            axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
            return;
        }

        getData();

    }, []); 


  return (
    <main className={Style.main}>
      <BrowserRouter>  
        <Header user={user} login={login} setUser={setUser} setLogin={setLogin} data={data} getData={getData}/>
        <div className={Style.body}>
          <Routes>
            <Route index element={<Main user = { login } />}/>
            <Route path="/main" element={<Main user = { login }/>}/>
            <Route path="/signin" element={<Signin setUser = {setUser} setLogin = {setLogin}/>}/>
            <Route path="/signup" element={<Signup/>}/>
            <Route path="/profile" element={<Profile />}/>
            {/* 경로 이탕 시 */}
            <Route path='*' element={<NotFound />}/>
          </Routes>
        </div>
        </BrowserRouter>
    </main>
  );
}

export default App;
